mapper.select:
        SELECT COUNT(distinct(a.ci_auditoria))
        FROM tb_auditoria a
        INNER JOIN tb_sistema_aud s ON a.ci_auditoria = s.cd_auditoria
        WHERE a.ci_auditoria IS NOT NULL
        AND s.ci_sistema = #{id}
        AND NOT EXISTS (
	        SELECT 1
	        FROM tb_sistema_aud
	        WHERE 
	            ci_sistema = #{id}
	            AND tp_movimento = #{tipoMovimento}
        );
