mapper.select:

    SELECT COUNT(*)
    FROM tb_termo_condicao
    WHERE ci_termo_condicao = #{id}
    OR dt_criacao <= (
        SELECT dt_criacao
        FROM tb_termo_condicao
        WHERE ci_termo_condicao = #{id}
    )
    AND cd_sistema = (
        SELECT cd_sistema
        FROM tb_termo_condicao
        WHERE ci_termo_condicao = #{id}
    )


    SELECT *
    FROM tb_termo_condicao
    WHERE ci_termo_condicao = #{id}
    OR dt_criacao <= (
        SELECT dt_criacao
        FROM tb_termo_condicao
        WHERE ci_termo_condicao = #{id}
    )
    AND cd_sistema = (
        SELECT cd_sistema
        FROM tb_termo_condicao
        WHERE ci_termo_condicao = #{id}
    )
    ORDER BY dt_criacao DESC
    OFFSET #{offset} ROWS
    FETCH NEXT #{limit} ROWS ONLY
