mapper.select:
(SELECT t.ci_termo_condicao, s.nm_sistema, s.ci_sistema, t.ds_versao, t.dt_criacao, t.nm_usuario_cadastro FROM tb_termo_condicao t INNER JOIN tb_sistema s ON s.ci_sistema = t.cd_sistema WHERE s.nm_sistema = 'Portal da DÃ­vida Ativa' ORDER BY t.dt_criacao DESC LIMIT 1)
UNION ALL
(SELECT t.ci_termo_condicao, s.nm_sistema, s.ci_sistema, t.ds_versao, t.dt_criacao, t.nm_usuario_cadastro FROM tb_termo_condicao t INNER JOIN tb_sistema s ON s.ci_sistema = t.cd_sistema WHERE s.nm_sistema = 'Portal das Origens' ORDER BY t.dt_criacao DESC LIMIT 1);
